//package albert.codeforces.nikitaandstring877b; import java.util.Scanner; public class Solution {    public static void main(String[] args) {         //bbabbbbbaaba        Scanner scan = new Scanner(System.in);        String s = scan.next();        int n = s.length();        //int[] l2r = new int[n];        int[] r2l = new int[n];        //l2r[0] = (s.charAt(0) == 'a') ? 1 : 0;        r2l[n - 1] = (s.charAt(n - 1) == 'a') ? 1 : 0;        //int max = 0;        for (int i = 1; i < n; i++) {            //l2r[i] = l2r[i - 1] + ((s.charAt(i) == 'a') ? 1 : 0);            r2l[n - i - 1] = r2l[n - i] + ((s.charAt(n - i - 1) == 'a') ? 1 : 0);            //max = Math.max(max, Math.max(l2r[i], r2l[i]));        }        int endA = 0;        int endB = 0;        int ans = 0;        for (int i = 0; i < n; i++) {            if (s.charAt(i) == 'a') {                endA++;            } else {                endB = Math.max(endB, endA) + 1;            }            ans = Math.max(ans, Math.max(endA, endB) + ((i < n - 1) ? r2l[i + 1] : 0));        }        System.out.println(ans);    }}
