#   ==========     //\\       //||     ||====//||#       ||        //  \\        ||     ||   // ||#       ||       //====\\       ||     ||  //  ||#       ||      //      \\      ||     || //   ||#   ========== //        \\  ========  ||//====|| #  code def solve():    n = int(input())    a = 0    b = 1023     for i in range(n):        op, k = input().split()        k = int(k)        if op == '|':            a |= k            b |= k        if op == '^':            a ^= k            b ^= k        if op == '&':            a &= k            b &= k        p = 1    print(3)    aa = a    bb = b    ans = 0    for i in range(10):        if aa % 2 == 1 and bb % 2 == 0:            ans += p        p *= 2        aa //= 2        bb //= 2    print('^', ans)     p = 1    ans = 1023    aa = a    bb = b    for i in range(10):        if aa % 2 == 0 and bb % 2 == 0:            ans -= p        p *= 2        aa //= 2        bb //= 2    print('&', ans)     p = 1    ans = 0    aa = a    bb = b    for i in range(10):        if aa % 2 == 1 and bb % 2 == 1:            ans += p        p *= 2        aa //= 2        bb //= 2    print('|', ans)    return def main():    t = 1    # t = int(input())    for _ in range(t):        solve() if __name__ == "__main__":    main()
