//package SecondLesson;////import java.util.ArrayDeque;//import java.util.Queue;import java.util.Scanner; public class Tabletennis_CF {     public void run(){        Scanner in = new Scanner(System.in);        int n = in.nextInt(); // число участников        long k = in.nextLong(); // число побед для выигрыша        int x = in.nextInt(); // сила первого игрока        long m = 0; // текущее число побед         int y; // сила второго игрока         for (int i = 1; i < n; i++) {            y = in.nextInt(); // считывание силы второго игрока            if (m == k) // если                break;            if (x > y) // если сила первого больше силы второго,                m++; // то увеличиваем текущее число побед            else { // иначе                x = y; // перезаписываем победителя первого игрока в переменную первого                m = 1; // число побед уже начинается с единицы            }        }        System.out.println(x);                     }      public static void main(String[] args) {         new Tabletennis_CF().run();    }}
