import java.util.Scanner;public class p879B {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        long n=sc.nextInt(),k=sc.nextLong();        int a[]=new int[(int)n],max=-1,c=0;        for(int i=0;i<n;i++) a[i]=sc.nextInt();        for(int i=0;i<n;i++) {            if(a[i]>max) {                max=a[i];                c=0;            }            if(i>0) c++;            if(c>=k) break;        }        System.out.println(max);    }}
