import java.util.*;import java.io.*;public class Main {	public static void main(String[] args) throws IOException {		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		int n = Integer.parseInt(br.readLine());		StringTokenizer st = new StringTokenizer(br.readLine());		Integer[] visit = new Integer[n];		Set<Integer> cafes = new HashSet<Integer>();		for (int i = 0; i < n; ++i) {			visit[i] = Integer.parseInt(st.nextToken());			if (!cafes.contains(visit[i]))				cafes.add(visit[i]);		}		Map<Integer, Integer> last = new HashMap<Integer, Integer>();		for (int i = n - 1; i >= 0; --i) {			Integer v = last.getOrDefault(visit[i], null);			if (v == null)				last.put(visit[i], n - i);		}		Integer winner = null;		int winnerScore = 0;		for (Integer i : cafes) {			int score = last.get(i);			if (winner == null || winnerScore < score) {				winner = i;				winnerScore = score;			}		}		System.out.println(winner);	}}
