import java.util.*;public class A{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int a[]=new int[n];        HashMap<Integer,Integer> hm=new HashMap<>();        for(int i=0;i<n;i++)        {            a[i]=sc.nextInt();            hm.put(a[i],i+1);        }        int min=Integer.MAX_VALUE;        int ans=0;        for(Map.Entry<Integer,Integer> entry:hm.entrySet())        {            int x=entry.getKey();            int y=entry.getValue();            if(y<min)            {                min=y;                ans=x;            }        }        System.out.println(ans);    }}
