import java.util.*; public class Main{	public static void main(String[] args) {	    int count=0;		Scanner scan=new Scanner(System.in);		int n=scan.nextInt();				if(n>=1 && n<=1000)		{				int a[]=new int[n];		for(int i=0;i<n;i++)		{		    		    a[i]=scan.nextInt();		    if(a[i]<=1 && a[i]>=1000)		        break;		}		for(int i=1;i<n-1;i++)		{		   		     if(a[i]<a[i-1] && a[i]<a[i+1])		        count++;		    else if(a[i]>a[i-1] && a[i]>a[i+1])		        count++;		 		  		}				  System.out.println(count);  		}			}}
