# link: https://codeforces.com/problemset/problem/888/Bif __name__ == "__main__":    moves_count = int(input())    moves = input()    x = 0    y = 0    for direction in moves:        if direction == 'U':            y += 1        elif direction == 'D':            y -= 1        elif direction == 'L':            x -= 1        else:            x += 1    if x==0 and y==0:        print(moves_count)    else:        wrong_moves = abs(x) + abs(y)        print(moves_count - wrong_moves)                        
