#include<bits/stdc++.h>using namespace std;typedef long long int ll;const int maxn=2005,mod = 998244353;int A[maxn];int main(){	int n,one=0;	cin>>n;	for(int i=0;i<n;i++){		cin>>A[i];		one+=(A[i]==1);		}	if(one){		cout<<n-one;		return 0;	}	int ans=INT_MAX;	for(int i=0;i<n;i++){		int f=A[i];		for(int j=i+1;j<n;j++){			f=__gcd(f,A[j]);			if(f==1){				ans = min(ans,j-i);			}		}	}	if(ans==INT_MAX){		cout<<-1;		return 0;	}	ans+=(n-1);	cout<<ans;	return 0;}
