import java.util.*;public class problem{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int m=sc.nextInt();        String s=sc.next();        char[] a=new char[n];         for(int i=0;i<n;i++)        {            a[i]=s.charAt(i);        }        int l,r;        char c1,c2;        for(int i=0;i<m;i++)        {            l=sc.nextInt();            r=sc.nextInt();            c1=sc.next().charAt(0);            c2=sc.next().charAt(0);            for(int j=l-1;j<=r-1;j++)            {                if(a[j]==c1)                {                    a[j]=c2;                }            }        }        for(int j=0;j<n;j++)        {            System.out.print(a[j]);        }    }}
