    import java.util.*;    public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        int m=s.nextInt();        s.nextLine();                String input=s.nextLine();        String result="",result1="";        for(int i=0;i<m;i++){            int l=s.nextInt();            int r=s.nextInt();            l=l-1;            r=r-1;            result="";            char c1=s.next().charAt(0);            char c2=s.next().charAt(0);            for(int y=0;y<input.length();y++){                if(y>=l && y<=r){                    if(input.charAt(y)==c1){                        result=result+c2;                    }                    else                        result=result+input.charAt(y);                }                else                    result=result+input.charAt(y);            }            input=result;        }        System.out.println(result);            }}    
