# codeforcesif __name__=="__main__":    rows,col=map(int,input().split())    li=[]    ans=""    for i in range(rows):        s=input()        li.append(s)    for i in range(rows):        for j in range(col):            ind=li[i].index(li[i][j])            if li[i].count(li[i][j])==1:                count=0                for k in range(rows):                    if li[k][ind]==li[i][j] and k!=i:                        count=count+1                if count==0:                    ans+=li[i][j]    print(ans)        
