import java.util.Scanner;public class Main{    public static void main(String[] args){        Scanner scan = new Scanner(System.in);		int n = scan.nextInt();		int i, positive = 0, negative = 0;		long x;		for(i = 0; i < n; i ++){			x = scan.nextLong();			if(x > 0)				positive ++;			if(x < 0)				negative ++;			scan.nextLong();		}		if(positive < 2 || negative < 2)			System.out.println("Yes");		else System.out.println("No");	}}
