#include <iostream>#include <cstdio>#include <cstring>using namespace std;const int maxn = 5e5+10;int n,m;int pos[maxn];int dx[4] = {0,1,0,-1}, dy[4] = {1,0,-1,0};bool check(int i,int j){    int x1 = (i - 1) / m + 1;    int y1 = (i - 1) % m + 1;    int x2 = (j - 1) / m + 1;    int y2 = (j - 1) % m + 1;    for(int k = 0; k < 4; k++){        if(x1 + dx[k] == x2 && y1 + dy[k] == y2)            return 1;    }    return 0;} bool dfs(int i){    if(i == n * m + 1)        return 1;    int x = (i - 1) / m + 1, y = (i - 1) % m + 1;    for(int j = i; j <= n * m; j++){        swap(pos[i],pos[j]);        if(x != 1 && check(pos[i],pos[(x - 2) * m + y]))            continue;        if(y != 1 && check(pos[i],pos[(x - 1) * m + y - 1]))            continue;        if(dfs(i+1))            return 1;        swap(pos[i],pos[j]);    }    return 0;} int main(){    scanf("%d%d",&n,&m);    for(int i = 1; i <= n; i++){        for(int j = 1; j <= m; j++){            pos[(i - 1) * m + j] = (i - 1) * m + j;        }    }    if(!dfs(1)){        puts("NO");        return 0;    }    puts("YES");    for(int i = 1; i <= n; i++){        for(int j = 1; j <= m; j++){            if(j == 1)                printf("%d",pos[(i - 1) * m + j]);            else                printf(" %d",pos[(i - 1) * m + j]);        }        puts("");    }    return 0;}Â 	  	 	  	 	  	 	  			    		 			
