# include <iostream>#include <bits/stdc++.h>using namespace std; int func(string A, string B){	vector<vector<int> > pos(26);	for(int i=0;i<A.length();i++)	{		pos[A[i]-'a'].push_back(i);	}	int ans = 0;	int lastpos = A.length()+1;	for(int i=0;i<B.length();i++)	{		if(pos[B[i]-'a'].size()==0)return -1;		else 		{			if(pos[B[i]-'a'].back()<=lastpos)			{				ans++;				lastpos = pos[B[i]-'a'][0];			}			else			{				lastpos = *lower_bound(pos[B[i]-'a'].begin(), pos[B[i]-'a'].end(), lastpos+1);			}		}	}	return ans;} int main() {	ios::sync_with_stdio(false);	cin.tie(0);cout.tie(0);	string A="", B="";	cin >> A >> B;	cout << func(A, B) << endl;	return 0;}
