//import java.lang.*;import java.util.*; public class Main { 	public static void main(String[] args) {		Scanner scanner = new Scanner(System.in);		int n = scanner.nextInt();		int d = scanner.nextInt();		String s = scanner.next();		int [] road = new int[n+1];		for (int i = 0; i < s.length(); ++i)			road[i+1] = s.charAt(i) - '0';				int frog = 1;		int cnt = 0;		while(frog != n)		{			boolean canJump = false;			for (int i = frog + d; i >= frog + 1; --i)			{				if (i > n)					i = n;				if (road[i] == 1)				{					canJump = true;					cnt++;					frog = i;					break;				}			}			if(!canJump)			{				cnt = -1;				break;			}		}		System.out.println(cnt);		scanner.close();	} // end main	}		    		 	  		 	  	     				  	
