import java.util.*;public class PerfectSquare{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int a[] = new int[n];        int max=-(int)Math.pow(10,6);        for(int i =0;i<n;i++)        {            a[i]=sc.nextInt();            if(!isPerfect(a[i]))            {                if(a[i]>max)                {                    max=a[i];                }            }        }        System.out.println(max);    }    static boolean isPerfect(int n)    {        double b = Math.sqrt(n);        if(b==(int)b)        return true;        else        return false;    }}
