import java.util.Scanner;public class Main{	public static boolean notSqr(int num){		return (num < 0)?true:Math.sqrt(num) > (int)Math.sqrt(num);	}    public static void main(String[] args){        Scanner scan = new Scanner(System.in);		int n = scan.nextInt(), i, k = -1;		int[] a = new int[1000];		for(i = 0; i < n; i ++){			a[i] = scan.nextInt();			if(notSqr(a[i]))				k = (k == -1)?i:((a[i] > a[k])?i:k);		}		System.out.println(a[k]);	}}
