import java.util.*;import java.io.*;/** * * @author alanl */public class Main{    static BufferedReader input = new BufferedReader(new InputStreamReader(System.in));    static StringTokenizer st;    static PrintWriter pr = new PrintWriter(new BufferedWriter(new OutputStreamWriter(System.out)));    public static void main(String[] args) throws IOException{        int n = readInt(), idx = readInt(), l = readInt(), r = readInt();        if(l==1){            if(r==n){                println(0);                return;            }            else{                println(Math.abs(idx-r)+1);                return;            }        }        else if(r==n){            println(Math.abs(idx-l)+1);            return;        }        else if(idx<l){            println(r-idx+2);            return;        }        else if(idx>r){            println(idx-l+2);            return;        }        else println(r-l+2+Math.min(r-idx, idx-l));    }    static String next () throws IOException {        while (st == null || !st.hasMoreTokens())                st = new StringTokenizer(input.readLine().trim());        return st.nextToken();    }    static long readLong () throws IOException {        return Long.parseLong(next());    }    static int readInt () throws IOException {        return Integer.parseInt(next());    }    static double readDouble () throws IOException {        return Double.parseDouble(next());    }    static char readChar () throws IOException {        return next().charAt(0);    }    static String readLine () throws IOException {        return input.readLine().trim();    }    static void print(Object b) {        System.out.print(b);    }    static void println(Object b) {        System.out.println(b);    }    static void println() {        System.out.println();    }}
