import java.util.*;import java.util.stream.*; public class Solution {	public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int x = scan.nextInt();		int hh = scan.nextInt();		int mm = scan.nextInt();		int result = 0;				while(!happy(hh, mm)) {			if(mm >= x) {				mm -= x;			} else {				mm = 60 - (x - mm);				if(hh > 0) {					hh--;				} else {					hh = 23;				}			}			result++;		}		System.out.println(result);	}		private static boolean happy(Integer hh, Integer mm) {		return hh.toString().contains("7") || mm.toString().contains("7");	}}
