import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);        int x = sc.nextInt();        int h = sc.nextInt();        int m = sc.nextInt();        int c = 0;        while(h % 10 != 7 && m % 10 != 7){            c++;            m -= x;            if(m < 0){                m += 60;                h -= 1;            }            if (h<0)                h = 23;        }        System.out.println(c);			}}
