#codeforcesif __name__=="__main__":    x=int(input())    hh,mm=map(int,input().split())    c=0    while '7'not in str(hh) and '7'not in str(mm):        if mm!=0:            if mm-x<0:                if hh==00:                    hh=24                hh=hh-1                mm=mm+60            mm=mm-x            c=c+1        else:            if hh==00:                hh=24            hh=hh-1            if hh==7 or hh==17:                c=c+1            mm=60    print(c)     
