import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            String s=input.next();            int g[][]=new int[s.length()][s.length()];            for(int i=0;i<s.length();i++)            {                int sum=0;                int f=1;                for(int j=i;j<s.length();j++)                {                    if(s.charAt(j)==')')                    {                        sum--;                    }                    else                    {                        sum++;                    }                    if(sum<0)                    {                        f=0;                    }                    g[i][j]=f;                }            }            int p[][]=new int[s.length()][s.length()];            for(int i=0;i<s.length();i++)            {                int sum=0;                int f=1;                for(int j=i;j>=0;j--)                {                    if(s.charAt(j)=='(')                    {                        sum--;                    }                    else                    {                        sum++;                    }                    if(sum<0)                    {                        f=0;                    }                    p[j][i]=f;                }            }            int ans=0;            for(int i=0;i<s.length();i++)            {                for(int j=i;j<s.length();j++)                {                    int len=j-i+1;                    if(len%2==0)                    {                        if(g[i][j]==1 && p[i][j]==1)                        {                            ans++;                        }                    }                }            }            out.println(ans);        }        out.close();    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str="";            try            {                str=br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
