import java.io.*;import java.util.*;public class Main {    public static void main(String args[])    {        FastReader input=new FastReader();        PrintWriter out=new PrintWriter(System.out);        int T=1;        while(T-->0)        {            int n=input.nextInt();            int x[]=new int[n];            for(int i=0;i<n;i++)            {                x[i]=input.nextInt();            }            mergeSort(x,0,n-1);            int g=0;            ArrayList<Integer> list=new ArrayList<>();            for(int i=1;i<n;i++)            {                int d=x[i]-x[i-1];                list.add(d);                g=gcd(g,d);            }            long sum=0;            for(int i=0;i<list.size();i++)            {                int d=list.get(i);                d/=g;                sum+=d-1;            }            out.println(sum);        }        out.close();    }    public static int gcd(int a, int b)    {        if(a==0)        {            return b;        }        else        {            return gcd(b%a,a);        }    }    public static void mergeSort(int a[],int p,int r)    {        if(p<r)        {            int q=(p+r)/2;            mergeSort(a,p,q);            mergeSort(a,q+1,r);            merge(a,p,q,r);        }    }    public static void merge(int a[],int p,int q,int r)    {        int n1=q-p+2;        int L[]=new int[n1];        int n2=r-q+1;        int R[]=new int[n2];        for(int i=p;i<=q;i++)        {            L[i-p]=a[i];        }        L[n1-1]=Integer.MAX_VALUE;        for(int i=q+1;i<=r;i++)        {            R[i-q-1]=a[i];        }        R[n2-1]=Integer.MAX_VALUE;        int x=0,y=0;        for(int i=p;i<=r;i++)        {            if(L[x]<=R[y])            {                a[i]=L[x];                x++;            }            else            {                a[i]=R[y];                y++;            }        }    }    static class FastReader    {        BufferedReader br;        StringTokenizer st;        public FastReader()        {            br = new BufferedReader(new InputStreamReader(System.in));        }        String next()        {            while (st == null || !st.hasMoreElements())            {                try                {                    st = new StringTokenizer(br.readLine());                }                catch (IOException e)                {                    e.printStackTrace();                }            }            return st.nextToken();        }        int nextInt()        {            return Integer.parseInt(next());        }        long nextLong()        {            return Long.parseLong(next());        }        double nextDouble()        {            return Double.parseDouble(next());        }        String nextLine()        {            String str="";            try            {                str=br.readLine();            }            catch (IOException e)            {                e.printStackTrace();            }            return str;        }    }}
