import java.util.*;import java.lang.*;import java.io.*; public final class Codeforces{    static HashMap<Integer,Integer> map;    static public void dfs(LinkedList<Integer> adj[] , int cur, boolean[] visited,int u)    {        visited[u]=true;        if(map.containsKey(cur))        map.replace(cur , map.get(cur)+1);        else        map.put(cur,1);                for(int v : adj[u])        {            if(!visited[v])            dfs(adj , cur+1, visited , v);        }    }    public static void main (String[] args) throws java.lang.Exception	{	    	    Scanner sc = new Scanner(System.in);	    int N = sc.nextInt();	    map = new HashMap();	    LinkedList<Integer> adj[] = new LinkedList[N+1];	    for(int i=1;i<=N;i++)adj[i]=new LinkedList();	    int j=2;	    while(j<=N)	    {	        adj[sc.nextInt()].add(j);	        j++;	    }	    boolean[] visited = new boolean[N+1];	    dfs(adj,1,visited,1);	    int c=0;	    for(int k : map.keySet())	    {	        if(map.get(k)%2!=0)c++;	    }	    System.out.println(c);	}}
