//stan hu tao//join nct ridin by first year culture repsimport static java.lang.Math.max;import static java.lang.Math.min;import static java.lang.Math.abs;import static java.lang.System.out;import java.util.*;import java.io.*;import java.math.*;Â public class x930B{    public static void main(String hi[]) throws Exception    {        BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));        char[] arr = infile.readLine().toCharArray();        int N = arr.length;        int[][][] guesses = new int[26][N][26];        for(int head=0; head < N; head++)            for(int i=1; i < N; i++)            {                int loc = (head+i)%N;                guesses[arr[head]-'a'][i][arr[loc]-'a']++;            }        int[] freq = new int[26];        for(char c: arr)            freq[c-'a']++;        double res = 0.0;        for(int a=0; a < 26; a++)        {            int[][] grid = guesses[a];            double temp = 0.0;            for(int i=0; i < N; i++)            {                int cnt = 0;                int total = 0;                for(int b=0; b < 26; b++)                {                    if(grid[i][b] == 1)                        cnt++;                    total += grid[i][b];                }                if(total > 0)                    temp = max(temp, 1.0*cnt/total);            }            res += (1.0*freq[a]/N)*temp;        }        System.out.println(res);    }}
