import java.io.*;import java.math.*;import java.util.*; public class Main {	public static void main(String[] args) {		FastScanner sc = new FastScanner();		PrintWriter pw = new PrintWriter(System.out);				String s = sc.next();		int N = s.length();		int[] nums = new int[N];		for (int i = 0; i < N; i++)			nums[i] = s.charAt(i)-'a';				int[][][] cnt = new int[26][N][26];		for (int i = 0; i < N; i++) {			for (int j = 0; j < N; j++) {				int k = (j-i+N)%N;				cnt[nums[i]][k][nums[j]] += 1;			}		}		int[][] winRates = new int[26][N];		for (int i = 0; i < N; i++) {			int good = 0;			for (int k = 0; k < N; k++) {				int j = (i+k)%N;				if (cnt[nums[i]][k][nums[j]]==1)					winRates[nums[i]][k] += 1;			}		}				int un = 0;		for (int i = 0; i < 26; i++) {			int max = 0;			for (int j = 0; j < N; j++)				max = Math.max(max, winRates[i][j]);			un += max;		}		pw.println((un+0.0)/N);		pw.close();	}	    static class FastScanner {         BufferedReader br;         StringTokenizer st;           public FastScanner() {             br = new BufferedReader(new InputStreamReader(System.in));         }           String next() {             while (st == null || !st.hasMoreElements()) {                 try {                     st = new StringTokenizer(br.readLine());                } catch (IOException  e) {                     e.printStackTrace();                 }             }             return st.nextToken();         }                int ni() {             return Integer.parseInt(next());         }          long nl() {             return Long.parseLong(next());         }           double nd() {             return Double.parseDouble(next());         }           String nextLine() {            String str = "";             try {                 str = br.readLine();             } catch (IOException e) {                e.printStackTrace();             }             return str;        }    }}
