import java.io.*;import java.util.*;import java.text.*;import java.math.*;import java.util.regex.*;public class Main{    public static void main(String[] args) {        Scanner s=new Scanner(System.in);        int n=s.nextInt();        String str=s.next();               char[] ch=str.toCharArray();         System.out.print(ch[0]);        for(int i=1;i<ch.length;i++)        if(check(ch[i])==0)        System.out.print(ch[i]);        else{            if(check(ch[i-1])==0)            System.out.print(ch[i]);        }           }    public static int check(char x)    {    if(x=='a' || x=='e' || x=='i' || x=='o' || x=='u' || x=='y')    return 1;    return 0;    }}
