//import java.lang.*;import java.util.*; public class Main { 	public static void main(String[] args) {		Scanner scanner = new Scanner(System.in);		int n = scanner.nextInt();		int [] arr = new int[n];		for (int i = 0; i < n; ++i)		{			arr[i] = scanner.nextInt();		}				int friendPos = 1000000;		int myPos = 0;		int half = 1000000 / 2;		for (int i = 0; i < n; ++i)		{			if (arr[i] > half)			{				friendPos = arr[i];				if (i != 0)					myPos = arr[i-1];				break;			}			myPos = arr[i];		}				if (1000000 - friendPos > myPos - 1)			System.out.println(1000000 - friendPos);		else			System.out.println(myPos - 1);		scanner.close();	} // end main	}
