// package practice; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.HashMap;import java.util.Map; public class LooveTriangle2 { 	static Map<Integer, Integer> map = new HashMap<>(); 	public static void main(String[] args) throws NumberFormatException, IOException { 		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		br.readLine();		String[] sr = br.readLine().split(" ");		for (int i = 0; i < sr.length; i++) {			map.put(i + 1, Integer.parseInt(sr[i]));		}		String res = "NO";		for (int i = 0; i < sr.length; i++) {			int num = p(i + 1);			num = p(num);			num = p(num);			if (num == i + 1) {				res = "YES";				break;			}		}		System.out.println(res);	} 	private static int p(int i) {		if (map.get(i) == null) {			return Integer.MIN_VALUE;		}		return map.get(i);	} }
