import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        long n=s.nextLong();        int k=s.nextInt();        long a[]=new long[k];        long ans=0,min=Long.MAX_VALUE;int index=0;        for(int i=0;i<k;i++)            a[i]=s.nextLong();        for(int j=0;j<k;j++){            if(a[j]==n){                ans=1;                break;            }            else if(a[j]<n){                long o=n%a[j];                if(o<min){                    min=o;                    ans=n/a[j];                    index=j;                }            }        }        index++;        System.out.println(index+" "+ans);    }}
