//import java.lang.*;import java.util.*;Â public class Main {	public static void main(String[] args) {		Scanner scanner = new Scanner(System.in);		int r = scanner.nextInt();		int c = scanner.nextInt();		char[][] pasture = new char[r][c];		for (int i = 0; i < r; ++i)		{			String s = scanner.next();			for (int j = 0; j < c; ++j)				pasture[i][j] = s.charAt(j);		}				boolean cannot = false;		for (int i = 0; i < r; ++i)		{			for (int j = 0; j < c; ++j)			{				if (pasture[i][j] == 'S')				{					if(j - 1 >= 0)					{						if(pasture[i][j - 1] == '.')							pasture[i][j - 1] = 'D';						else if (pasture[i][j - 1] == 'W')						{							cannot = true;							break;						}					}					if(i - 1 >= 0)					{						if (pasture[i - 1][j] == '.')							pasture[i - 1][j] = 'D';						else if (pasture[i - 1][j] == 'W')						{							cannot = true;							break;						}					}					if(j + 1 < c)					{						if (pasture[i][j + 1] == '.')							pasture[i][j + 1] = 'D';						else if (pasture[i][j + 1] == 'W')						{							cannot = true;							break;						}					}					if(i + 1 < r)					{						if (pasture[i + 1][j] == '.')							pasture[i + 1][j] = 'D';						else if (pasture[i + 1][j] == 'W')						{							cannot = true;							break;						}					}				}			}			if (cannot)				break;		}		if (cannot)			System.out.println("No");		else		{			System.out.println("Yes");			for (int i = 0; i < r; ++i)			{				for (int j = 0; j < c; ++j)					System.out.print(pasture[i][j]);				System.out.print("\n");			}		}		scanner.close();	} // end main	}	    	 	  		  					 		 			   	
