import java.util.*;import java.util.Arrays;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int l=sc.nextInt();        int r=sc.nextInt();        int a=sc.nextInt();        int d=0, e=0;        if(l<r)        {            d=r-l;        }        else if(l>r)        {            e=l-r;        }        else        {            a=a/2;            l=l+a;            r=r+a;            a=0;        }        if(d!='0')        {            if(a>d)            {                l=l+d;                a=a-d;            }            else            {                l=l+a;                a=0;            }        }        if(e!='0')        {            if(a>e)            {                r=r+e;                a=a-e;            }            else            {                r=r+a;                a=0;            }        }        if(a!='0')        {            a=a/2;            l=l+a;            r=r+a;            a=0;        }        int b=Math.min(l,r);        System.out.println(2*b);    }}
