	// _________________________RATHOD_____________________________________________________________import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.Scanner; import java.util.StringTokenizer; import java.util.*;  public class Main {     static class FastReader     {         BufferedReader br;         StringTokenizer st;           public FastReader()         {             br = new BufferedReader(new                     InputStreamReader(System.in));         }           String next()         {             while (st == null || !st.hasMoreElements())             {                 try                {                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e)                 {                     e.printStackTrace();                 }             }             return st.nextToken();         }           int nextInt()         {             return Integer.parseInt(next());         }           long nextLong()         {             return Long.parseLong(next());         }           double nextDouble()         {             return Double.parseDouble(next());         }           String nextLine()         {             String str = "";             try            {                 str = br.readLine();             }             catch (IOException e)             {                 e.printStackTrace();             }             return str;         }     }       public static void main(String[] args)     {         FastReader sc=new FastReader();         int n=sc.nextInt();        int m=sc.nextInt();        long ar1[]=new long[n];        long ar2[]=new long[m];        for(int i=0;i<n;i++)        ar1[i]=sc.nextLong();        for(int i=0;i<m;i++)        ar2[i]=sc.nextLong();        int i,j;        i=-1;j=-1;        long sum1=0;        long sum2=0;        int ans=0;        while(i<n&&j<m)        {         if(sum1==sum2)         {             ans++;             sum1=0;             sum2=0;             i++;             j++;             if(i<n&&j<m)             {               sum1=ar1[i];                 sum2=ar2[j];              }                      }         else if(sum1<sum2)         {             i++;             sum1=sum1+ar1[i];         }         else         {            j++;             sum2=sum2+ar2[j];          }                 }        System.out.println(ans-1);                    }                       } 
