//I AM THE CREED/* //I AM THE CREED/* package codechef; // don't place package name! */import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.StringTokenizer; import java.util.*;import java.awt.Point;public class Main{        public static void main(String[] args) throws IOException     {                 FastScanner input = new FastScanner();        String s=input.next();        int[] freq=new int[26];        for(int i=0;i<s.length();i++){            freq[s.charAt(i)-'a']++;        }        int character=0;        int single=0;        for(int i=0;i<26;i++){            if(freq[i]==1){                single++;            }            character+=Math.min(freq[i], 1);        }        System.out.println((character==4 || (character==3 && single<3)||(character==2 && single==0))?"Yes":"No");                }    static boolean isPrime(int n)     {         // Corner case         if (n <= 1)             return false;           // Check from 2 to n-1         for (int i = 2; i < n; i++)             if (n % i == 0)                 return false;           return true;     }     static class FastScanner {		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st=new StringTokenizer("");		String next() {			while (!st.hasMoreTokens())				try {					st=new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			return st.nextToken();		}				int nextInt() {			return Integer.parseInt(next());		}		int[] readArray(int n) {			int[] a=new int[n];			for (int i=0; i<n; i++) a[i]=nextInt();			return a;		}		long nextLong() {			return Long.parseLong(next());		}	} }
