import java.util.Arrays;import java.util.HashMap;import java.util.Map;import java.util.Scanner; public class Main {	static int maxn=110;	static int cnt[]=new int[maxn];		public static void main(String[] args) {		Scanner in=new Scanner(System.in);		String s=in.next();		for(int i=0;i<s.length();i++) 				cnt[s.charAt(i)-'a']++;		int flag=0,flag2=0;		for(int i=0;i<26;i++) {			if(cnt[i]>1) flag++;			if(cnt[i]>=1) flag2++;		}		if(flag2>4) System.out.println("No"); 		else if(flag>=2||flag2>3||(flag==1&&flag2>=3)) System.out.println("Yes");		else System.out.println("No");	}}
