//I AM THE CREED/* //I AM THE CREED/* package codechef; // don't place package name! */import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.StringTokenizer; import java.util.*;import java.awt.Point;public class Main{    public static void main(String[] args) throws IOException     {           Scanner input = new Scanner(System.in);        while(input.hasNext()){            int n=input.nextInt();            int k1=input.nextInt();            int k2=input.nextInt();            int[] l=new int[n];            int[] m=new int[n];            for(int i=0;i<n;i++){                l[i]=input.nextInt();            }            for(int i=0;i<n;i++){                m[i]=input.nextInt();            }            PriorityQueue<Integer> pq=new PriorityQueue<>();            for(int i=0;i<n;i++){                pq.add(-Math.abs(l[i]-m[i])==0?Integer.MAX_VALUE:-Math.abs(l[i]-m[i]));            }            while(true){               int diff=pq.poll();               if(diff==Integer.MAX_VALUE || (k1==0 && k2==0)){                   pq.add(diff);                   break;               }               diff=-diff;               if(k1>k2){                   diff--;                   k1--;                   pq.add(-diff==0?Integer.MAX_VALUE:-diff);                   continue;               }               diff--;               k2--;               pq.add(-diff==0?Integer.MAX_VALUE:-diff);            }            long sum=0;            while(!pq.isEmpty()){                int ele=pq.poll();                ele=(ele==Integer.MAX_VALUE?0:ele);                long t=ele;                sum+=(t*t);            }            System.out.println(sum+(Math.abs(k1-k2)%2));        }    } }
