# -*- coding: utf-8 -*-"""Created on Mon Oct 19 22:22:28 2020 @author: Dark Soul"""from queue import PriorityQueue[n,k1,k2]=list(map(int,input().split()))a=list(map(int,input().split()))b=list(map(int,input().split())) diff=PriorityQueue()for i in range(n):    diff.put(-1*abs(a[i]-b[i]))while k1:    x=diff.get()    if x==0:        diff.put(0)        break    diff.put(x+1)    k1-=1while k2:    x=diff.get()    if x==0:        diff.put(0)        break    diff.put(1+x)    k2-=1ans=0flag=0if (k1+k2)%2:    flag=1for i in range(n):    x=diff.get()    if i==n-1 and flag:        x+=1    ans+=x*xprint(ans)                                        
