import java.util.*;import java.io.*;public class PermuteDigits {    public static void main(String args[]) throws IOException{      Scanner in = new Scanner(System.in);      int n = in.nextInt(), a = in.nextInt(), b = in.nextInt(), c = in.nextInt(), t = in.nextInt(), sum = 0;      sum += n * a;      if (b >= c) System.out.println(sum);      else {          int lol = c - b;          for (int i = 0; i < n; i++) sum += lol * (t - in.nextInt());           System.out.println(sum);      }                }}
