import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        s.nextLine();        String s2=s.nextLine();        String[] s1=s2.split(" ");        ArrayList<String> a=new ArrayList<>();        for(int i=0;i<n;i++){            String s3="";            char[] ch=s1[i].toCharArray();            Arrays.sort(ch);            for(int j=0;j<s1[i].length();j++){                char c=ch[j];                if(s3.indexOf(c)<0)                    s3=s3+c;            }            a.add(s3);        }                HashSet<String> h=new HashSet<>(a);                System.out.println(h.size());           }}   
