/** * * @author aditya */import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;import java.util.ArrayList;import java.util.Collections;import java.util.StringTokenizer; public class RemoveDuplicates { 	public static void main(String[] args) {            FastScanner fs=new FastScanner();            int T=1;            PrintWriter out=new PrintWriter(System.out);            for (int tt=0; tt<T; tt++) {                int n = fs.nextInt();                int arr[] = fs.readArray(n);                int count[] = new int[1001];                ArrayList<Integer> ans = new ArrayList<>();                for(int i=n-1;i>=0;i--){                    count[arr[i]]++;                    if(count[arr[i]]==1)                        ans.add(arr[i]);                }                System.out.println(ans.size());                Collections.reverse(ans);                for(int i:ans){                    System.out.print(i+" ");                }                System.out.println();            }            out.close();	}		static class FastScanner {            BufferedReader br=new BufferedReader(new InputStreamReader(System.in));            StringTokenizer st=new StringTokenizer("");            public String next() {            while (!st.hasMoreElements())		try {                    st=new StringTokenizer(br.readLine());		} catch (IOException e) {                    e.printStackTrace();		}		return st.nextToken();            }		            int[] readArray(int n) {            int[] a=new int[n];             for (int i=0; i<n; i++) a[i]=nextInt();		return a;            }            int nextInt () {                return Integer.parseInt(next());            }	} }
