import java.util.*;public class MyClass {    public static void main(String args[]) {    Scanner sc=new Scanner(System.in);    int n=sc.nextInt();    int[] arr=new int[n];    for(int i=0;i<n;i++)    {arr[i]=sc.nextInt();    }     LinkedHashSet h = new LinkedHashSet();    int[] arr1=new int[n];    int k=0;    for(int i=n-1;i>=0;i--)   { arr1[k]=arr[i];    k++;    }    for(int i=0;i<n;i++)    {        if(!h.contains(arr1[i]))        h.add(arr1[i]);    } ArrayList<Integer> w=                 new ArrayList<Integer>(h);                Collections.reverse(w);                System.out.println(w.size());                for(int i:w)                System.out.print(i+" ");    }}
