import java.util.*;import java.io.*;public class Solution {           public static PrintWriter out;    public static void main(String args[])    {       MyScanner sc=new MyScanner();        out = new PrintWriter(new BufferedOutputStream(System.out), true);       int n=sc.nextInt();       for(int z=1;z<=n;z++)       {       long p=sc.nextLong();       long q=sc.nextLong();       long b=sc.nextLong();       q=q/gcd(p,q);       b=gcd(b,q);       while(b!=1)       {          while(q%b==0)          q=q/b;          b=gcd(q,b);                  }              if(q==1)       out.println("Finite");       else       out.println("Infinite");       }             out.close();                  }   	public static long gcd(long a,long b)	{	    if(a==0)	    return b;	    else if(b==0)	    return a;	    return gcd(b,a%b);	}     public static class MyScanner     {        BufferedReader br;        StringTokenizer st;         public MyScanner() {            br = new BufferedReader(new InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() { return Integer.parseInt(next()); }        long nextLong() { return Long.parseLong(next()); }        double nextDouble() { return Double.parseDouble(next()); }                 String nextLine(){            String str = "";            try {                str = br.readLine();            } catch (IOException e) {                e.printStackTrace();            }            return str;        }    }}
