import java.util.*;public class MyClass {    public static void main(String args[]) {    Scanner sc=new Scanner(System.in);    int n=sc.nextInt();    int k=sc.nextInt();    int[] arr=new int[n];    HashSet<Integer> h=new HashSet<Integer>();    for(int i=0;i<n;i++)    {arr[i]=sc.nextInt();    h.add(arr[i]);    }    int p=0;    int[] a1=new int[n];    if(h.size()>=k)   {System.out.println("YES");     HashMap<Integer,Integer> hm=new HashMap<Integer,Integer>();    for(int i=0;i<n;i++)    {        if(!hm.containsKey(arr[i]))       { hm.put(arr[i],1);           a1[p]=i+1;           p++;       }    }    for(int i=0;i<k;i++)    System.out.print(a1[i]+" ");   }    else    System.out.println("NO");    }}
