//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String s[] = br.readLine().split(" ");        int n = Integer.parseInt(s[0]), m = Integer.parseInt(s[1]);        String str[] = br.readLine().split(" ");        int arr[] = new int[101], count = 0;        List<Integer> li = new ArrayList<>();        String res = "";        for(int i = 0; i<n&&count<m; i++)        {            if(arr[Integer.parseInt(str[i])]==0)            {                count++;                arr[Integer.parseInt(str[i])]++;                res = res + Integer.toString(i+1) + " ";            }        }        if(count<m) System.out.println("NO");        else         {            System.out.println("YES");            System.out.println(res);        }    }}
