//package javatiku; import com.sun.org.apache.bcel.internal.generic.BIPUSH;import com.sun.org.apache.xpath.internal.axes.SelfIteratorNoPredicate;import org.omg.PortableInterceptor.SYSTEM_EXCEPTION;import sun.awt.image.BufferedImageGraphicsConfig; import java.math.*;import java.math.BigDecimal;import java.math.BigInteger;import java.util.Scanner;import java.math.*;import java.util.*;public class Main{    public static void main(String[] args) {        //BigDecimal eqa= new BigDecimal((1 + Math.sqrt(5.0)) / 2.0);        Scanner sc = new Scanner(System.in);        HashSet<Integer> st=new HashSet<Integer>();        int n=sc.nextInt();        for(int i=1;i<=n;i++)        {            int x=sc.nextInt();            st.add(x);        }        st.remove(0);        System.out.println(st.size());    }     private static BigDecimal sqrt(BigDecimal x,int n) {        BigDecimal l=BigDecimal.ZERO,r=x,mid;        BigDecimal two=BigDecimal.valueOf(2);        for(int i=0;i<=n;i++) {            mid=l.add(r).divide(two);            if(mid.pow(2).compareTo(x)<=0)l=mid;            else r=mid;        }        return l;    }}
