//Code by Sounak, IIESTimport java.io.*;import java.math.*;import java.util.*;import java.util.Arrays;public class Test1{    public static void main(String args[])throws IOException{        FastReader sc = new FastReader();        StringBuilder sb = new StringBuilder();        long l=sc.nextLong();        long r=sc.nextLong();        long x=sc.nextLong();        long y=sc.nextLong();        long i,count=0;        for(i=1;i*i<=y/x;i++)        {            if((y/x)%i==0)            {                long a=i;                long b=(y/x)/i;                if(gcd(a,b)==1 && (a*x)>=l && (a*x)<=r && (b*x)>=l && (b*x)<=r)                {                    count+=2;                    if(a==b)                    count--;                }            }        }        if(y%x!=0)        count=0;        System.out.println(count);    }    public static long gcd(long a, long b)     {         if (a == 0)             return b;                   return gcd(b%a, a);     }}  class FastReader {    BufferedReader br;    StringTokenizer st;     public FastReader() { br = new BufferedReader(new InputStreamReader(System.in)); }    String next() {        while (st == null || !st.hasMoreElements()) {            try {                st = new StringTokenizer(br.readLine());            } catch (IOException e) {                e.printStackTrace();            }        }        return st.nextToken();    }    int nextInt() { return Integer.parseInt(next()); }    long nextLong() { return Long.parseLong(next()); }    double nextDouble() { return Double.parseDouble(next()); }    String nextLine() {        String str = "";        try {            str = br.readLine();        } catch (IOException e) {            e.printStackTrace();        }        return str;    }}
