#include <bits/stdc++.h>#define pb push_back#define sz(a) int(a.size())using namespace std;typedef long long ll; int read() {    char ch=getchar();int x=0,f=1;    while(ch<'0'||ch>'9') {if(ch=='-')f=-1;ch=getchar();}    while(ch>='0'&&ch<='9') {x=x*10+ch-'0';ch=getchar();}    return x*f;} //const int N=; struct pa {   int l,r,d,u;}p[2]; bool in1(int x,int y) {    if(x<p[0].l||x>p[0].r) return false;    if(y<p[0].d||y>p[0].u) return false;    return true;} int cut(int x,int y,int x1,int y1,int x2,int y2) {    if(x1<x2) {        swap(x1,x2),swap(y1,y2);    }    if((y1-y2)*x-y1*x2+x1*y2>(x1-x2)*y) return -1;    if((y1-y2)*x-y1*x2+x1*y2==(x1-x2)*y) return 0;    if((y1-y2)*x-y1*x2+x1*y2<(x1-x2)*y) return 1;} bool in2(int x,int y) {    int l=p[1].l,r=p[1].r,d=p[1].d,u=p[1].u;    if(cut(x,y,l,(u+d)/2,(l+r)/2,d)<0) return false;    if(cut(x,y,l,(u+d)/2,(l+r)/2,u)>0) return false;    if(cut(x,y,r,(u+d)/2,(l+r)/2,u)>0) return false;    if(cut(x,y,r,(u+d)/2,(l+r)/2,d)<0) return false;    return true;} int main() {#ifndef ONLINE_JUDGE    freopen("in.txt","r",stdin);#endif    for(int i=0;i<=1;i++) {        p[i]=(pa){101,-101,101,-101};        for(int j=1;j<=4;j++) {            int x=read(),y=read();            p[i].l=min(p[i].l,x);            p[i].r=max(p[i].r,x);            p[i].d=min(p[i].d,y);            p[i].u=max(p[i].u,y);        }    }    int l=p[1].l,r=p[1].r,d=p[1].d,u=p[1].u;    if(in1(l,(u+d)/2)) {printf("YES\n");return 0;};    if(in1(r,(u+d)/2)) {printf("YES\n");return 0;};    if(in1((l+r)/2,d)) {printf("YES\n");return 0;};    if(in1((l+r)/2,u)) {printf("YES\n");return 0;};    if(in1((l+r)/2,(u+d)/2)) {printf("YES\n");return 0;};    l=p[0].l,r=p[0].r,d=p[0].d,u=p[0].u;    if(in2(l,d)) {printf("YES\n");return 0;};    if(in2(l,u)) {printf("YES\n");return 0;};    if(in2(r,d)) {printf("YES\n");return 0;};    if(in2(r,u)) {printf("YES\n");return 0;};    if(in2((l+r)/2,(u+d)/2)) {printf("YES\n");return 0;};    printf("NO\n");    return 0;}
