#include <bits/stdc++.h>using namespace std;#define ll long long#define pi 3.14159265#define mod 1000000007#define pb push_backconst int N = 5e6+9;#define FAST  ios::sync_with_stdio(false);cin.tie(0);cout.tie(0);#define FF fflush(stdout);#define lcm(a,b) (a*b)/__gcd(a,b)int main(){    ll n,i,j,c=0;    cin >> n;    n*=2;    vector<ll>a(n);    for(i=0;i<n;i++)cin >> a[ i ];    for(i=0;i<n-1;i++)    {        if(a[ i ]!=a[ i+1 ])        {            for(j=i+1;j<n;j++)            {                if(a[ i ]==a[ j ])                {                    for(ll k=j;k>i+1;k--)swap(a[ k ],a[k-1]);                    c+=j-i-1;                    break;                }            }        }        else i++;    }    cout << c << endl;}
